mapper.select:
select t1.id dor_id, t1.name dor_name, t1.address dor_address, t1.postcode dor_postcode, t1.equipment dor_equipment, t1.service dor_service, t1.currency dor_currency, t1.weekPrice dor_weekPrice, t1.salePrice dor_salePrice, t1.additionalPrice dor_additionalPrice, t1.promotion dor_promotion, t1.latitude dor_latitude, t1.longitude dor_longitude, t1.description dor_description, t1.refund dor_refund, t1.question dor_question, t1.feature dor_feature, t1.city_id dor_cityId, t1.status dor_status, t1.display_order dor_displayOrder, t2.name dor_city, ifnull(order_count.dormitory_order_count, 0), coalesce(avg(t3.score), 0) rating,
ifnull(t8.distance, 0) as distance,
t9.id dor_companyId, t9.name dor_company from dormitory t1 left join view_dormitory_order_count as order_count on order_count.dormitory_id = t1.id left join city t2 on t2.id = t1.city_id left outer join dormitory_rating t3 on t3.dormitory_id = t1.id
left join distance_result t8 on t8.dormitory_id = t1.id and t8.college_id = #{collegeId}
left join company t9 on t1.company_id = t9.id
and t1.status = #{status}
and t1.status != #{excludeStatus}
group by t1.id
order by distance

select t1.id dor_id, t1.name dor_name, t1.address dor_address, t1.postcode dor_postcode, t1.equipment dor_equipment, t1.service dor_service, t1.currency dor_currency, t1.weekPrice dor_weekPrice, t1.salePrice dor_salePrice, t1.additionalPrice dor_additionalPrice, t1.promotion dor_promotion, t1.latitude dor_latitude, t1.longitude dor_longitude, t1.description dor_description, t1.refund dor_refund, t1.question dor_question, t1.feature dor_feature, t1.city_id dor_cityId, t1.status dor_status, t1.display_order dor_displayOrder, t2.name dor_city, coalesce(avg(t3.score), 0) rating, t4.id ri_id, t4.dormitory_id ri_dormitoryId, t4.name ri_name, t4.room_type_id ri_roomTypeId, t4.equipment ri_equipment, t4.service ri_service, t4.description as ri_description, t4.checkin_date ri_checkinDate, t4.orientations ri_orientation, t4.floors ri_floors, t4.bed_type ri_bedType, t4.house_area ri_houseArea, t4.ensuite_bathroom ri_ensuitBathroom, t4.kitchen_people_number ri_kitchenPeople, t4.floor_arrangement ri_floorArrange, t4.orientation_arrangement ri_orientationArrange, t4.room_language_arrangement ri_roomLanguageArrange, t4.kitchen_equipment ri_kitchenEquipment, t4.bathroom_equipment ri_bathroomEquipment, t4.status ri_status, t5.id rp_id, t5.room_info_id rp_roomInfoId, t5.contract_type_id rp_contractId, t5.currency rp_currency, t5.weekPrice rp_weekPrice, t5.salePrice rp_salePrice, t5.status rp_status, t6.name ri_roomType, t7.name rp_contract,
ifnull(t8.distance, 0) as distance,
t9.id dor_companyId, t9.name dor_company from dormitory t1 left join city t2 on t2.id = t1.city_id left outer join dormitory_rating t3 on t3.dormitory_id = t1.id
left join (select * from room_info where dormitory_id = #{id}) t4 ON t4.dormitory_id = t1.id
left join room_info t4 ON t4.dormitory_id = t1.id
left join (select * from room_price where room_info_id in (select id from room_info where dormitory_id = 15)) t5 on t5.room_info_id = t4.id
left join room_price t5 on t5.room_info_id = t4.id
left join room_type t6 on t6.id = t4.room_type_id left join contract_type t7 ON t7.id = t5.contract_type_id
left join distance_result t8 on t8.dormitory_id = t1.id and t8.college_id = #{collegeId}
left join company t9 on t1.company_id = t9.id
and t5.status != '-1'
and t1.status = #{status}
and t1.status != #{excludeStatus}
and t4.status = #{roomStatus}
and t4.status != #{excludeRoomStatus}
and t5.status != #{excludeRoomPriceStatus}
group by t5.id

