mapper.select:
select distinct t1.dormitory_id dormitoryId, t2.name from browsing_history t1 left join dormitory t2 on t1.dormitory_id = t2.id where t1.user_id = #{userId}
and t1.dormitory_id != #{id}
order by t1.id desc limit 0, 5

mapper.update:
update dormitory_rating set
	score = #{point},
	comment = #{comment},
	alias = #{alias},
	status = #{status}
where id = #{id}

mapper.select:
select id dr_id, dormitory_id dr_dormitoryId, user_id dr_userId, alias dr_alias, score dr_point, comment dr_comment, create_time dr_createTime, status dr_status
from dormitory_rating
where dormitory_id = #{dormitoryId}
