mapper.insert:
insert into slide (`desc`, path, `index`, status) values (
#{desc,javaType=string,jdbcType=VARCHAR},
#{path,javaType=string,jdbcType=VARCHAR},
#{index,javaType=int,jdbcType=INTEGER},
#{status,javaType=int,jdbcType=INTEGER}
)

mapper.update:
update slide set id = #{id}

	, `desc` = #{desc}


	, `index` = #{index}


	, status = #{status}

where id = #{id}

mapper.select:
select id, user_id userId, title, text textBody, create_time createTime, `type` type, cover_path coverPath, status
from article
where id = #{id}

mapper.insert:
insert into article (user_id, title, text, status, type, cover_path)
values (
#{userId}, #{title}, #{textBody}, #{status}, #{type}, #{coverPath}
)

mapper.select:
select id, title, cover_path coverPath from article
where 1=1

	and type = #{type}


	and status = #{status}

order by create_time desc
